const [movies, setMovies] = useState([])


const addmovie = movie => {
 setMovies([...movies, {id: uuidv4(), task: movie, completed:false, isEditing:false}])
}

  return (
    <div className="App"> 
    <Form addmovie={addmovie}/>
    </div>
  );
}

export default App;
